import React, { useState, useEffect } from 'react';
import { BrowserProvider, formatEther } from 'ethers';

const WalletConnect = () => {
  const [provider, setProvider] = useState(null);
  const [account, setAccount] = useState('');
  const [balance, setBalance] = useState('');
  const [chainId, setChainId] = useState('');
  const [isConnecting, setIsConnecting] = useState(false);
  const [error, setError] = useState('');

  const SEPOLIA_CHAIN_ID = '0xaa36a7';

  const connectWallet = async () => {
    setIsConnecting(true);
    setError('');

    try {
      console.log('Checking for MetaMask...');
      if (!window.ethereum) {
        throw new Error('Please install MetaMask!');
      }

      console.log('Creating provider...');
      const ethereumProvider = new BrowserProvider(window.ethereum);
      setProvider(ethereumProvider);

      console.log('Requesting accounts...');
      const accounts = await ethereumProvider.send('eth_requestAccounts', []);
      console.log('Accounts received:', accounts);
      const account = accounts[0];
      setAccount(account);

      // Rest of your code...
    } catch (err) {
      console.error('Detailed error:', err);
      setError(err.message);
    } finally {
      setIsConnecting(false);
    }
  };
  useEffect(() => {
    if (window.ethereum) {
      // Store the handlers as named functions so we can properly remove them
      const handleAccountsChanged = (accounts) => {
        if (accounts.length > 0) {
          setAccount(accounts[0]);
        } else {
          setAccount('');
          setBalance('');
        }
      };

      const handleChainChanged = (_chainId) => {
        window.location.reload();
      };

      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', handleChainChanged);

      return () => {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum.removeListener('chainChanged', handleChainChanged);
      };
    }
  }, []);
  const disconnectWallet = () => {
    setAccount('');
    setBalance('');
    setProvider(null);
  };

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Blockchain Voting System</h1>

      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-semibold mb-4">Ethereum Sepolia Connection</h2>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        {!account ? (
          <button
            onClick={connectWallet}
            disabled={isConnecting}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50"
          >
            {isConnecting ? 'Connecting...' : 'Connect MetaMask'}
          </button>
        ) : (
          <div className="space-y-4">
            <div className="bg-gray-100 p-4 rounded">
              <p className="text-gray-700">Connected Wallet Address:</p>
              <p className="font-mono break-all">{account}</p>
              {balance && (
                <p className="mt-2">
                  Balance: {parseFloat(balance).toFixed(4)} ETH
                </p>
              )}
            </div>
            <button
              onClick={disconnectWallet}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
            >
              Disconnect Wallet
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default WalletConnect;
