import React from 'react';

const WalletDetails = ({ account, balance, onDisconnect }) => (
  <div className="bg-gray-100 p-4 rounded space-y-4">
    <div>
      <p className="text-gray-700">Connected Wallet Address:</p>
      <p className="font-mono break-all">{account}</p>
    </div>
    {balance && (
      <div>
        <p className="text-gray-700">Balance:</p>
        <p className="font-mono">{parseFloat(balance).toFixed(4)} ETH</p>
      </div>
    )}
    <button
      onClick={onDisconnect}
      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
    >
      Disconnect Wallet
    </button>
  </div>
);

export default WalletDetails;
