import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import contractABI from '../abi/VotingSystem.json';

// const contractABI = [/* Paste the full ABI here */];
const contractAddress = "0x002A13DF3bB8Ce7D7143bE17223F8893e2152c29"; // Replace with actual address

export default function VotingApp() {
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState('');
  const [polls, setPolls] = useState([]);
  const [newPoll, setNewPoll] = useState({ title: '', options: [''], duration: 60 });

  useEffect(() => {
    connectWallet();
  }, []);

  async function connectWallet() {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const provider = new ethers.BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const votingContract = new ethers.Contract(contractAddress, contractABI, signer);

        setContract(votingContract);
        setAccount(accounts[0]);

        // Listen for events
        votingContract.on("PollCreated", (pollId, title, endTime, creator) => {
          loadPollDetails(pollId.toString());
        });
      } catch (err) {
        console.error("Connection error:", err);
      }
    }
  }

  async function loadPollDetails(pollId) {
    try {
      const details = await contract.getPollDetails(pollId);
      const results = await contract.getPollResults(pollId);
      setPolls(prev => [...prev, {
        id: pollId,
        title: details.title,
        options: details.options,
        endTime: details.endTime,
        creator: details.creator,
        results: results
      }]);
    } catch (err) {
      console.error("Error loading poll:", err);
    }
  }

  async function createPoll(e) {
    e.preventDefault();
    try {
      const tx = await contract.createPoll(
        newPoll.title,
        newPoll.options,
        newPoll.duration
      );
      await tx.wait();
      setNewPoll({ title: '', options: [''], duration: 60 });
    } catch (err) {
      console.error("Error creating poll:", err);
    }
  }

  async function castVote(pollId, optionIndex) {
    try {
      const tx = await contract.vote(pollId, optionIndex);
      await tx.wait();
      const results = await contract.getPollResults(pollId);
      setPolls(prev => prev.map(poll => 
        poll.id === pollId ? {...poll, results} : poll
      ));
    } catch (err) {
      console.error("Error voting:", err);
    }
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Decentralized Voting</h1>

      {/* Create Poll Form */}
      <form onSubmit={createPoll} className="mb-8">
        <input
          type="text"
          placeholder="Poll Title"
          value={newPoll.title}
          onChange={e => setNewPoll({...newPoll, title: e.target.value})}
          className="border p-2 mr-2"
        />
        {newPoll.options.map((option, i) => (
          <input
            key={i}
            type="text"
            placeholder={`Option ${i + 1}`}
            value={option}
            onChange={e => {
              const newOptions = [...newPoll.options];
              newOptions[i] = e.target.value;
              setNewPoll({...newPoll, options: newOptions});
            }}
            className="border p-2 mr-2"
          />
        ))}
        <button 
          type="button" 
          onClick={() => setNewPoll({...newPoll, options: [...newPoll.options, '']})}
          className="bg-green-500 text-white p-2 rounded"
        >
          Add Option
        </button>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded ml-2">
          Create Poll
        </button>
      </form>

      {/* Poll List */}
      <div className="grid grid-cols-1 gap-4">
        {polls.map(poll => (
          <div key={poll.id} className="border p-4 rounded">
            <h2 className="text-xl font-bold">{poll.title}</h2>
            <p>Created by: {poll.creator}</p>
            <p>Ends: {new Date(Number(poll.endTime) * 1000).toLocaleString()}</p>

            <div className="mt-4">
              {poll.options.map((option, i) => (
                <div key={i} className="flex items-center justify-between mb-2">
                  <div>
                    <button
                      onClick={() => castVote(poll.id, i)}
                      className="bg-blue-500 text-white p-2 rounded mr-2"
                    >
                      Vote
                    </button>
                    {option} - {poll.results[i]?.toString() || '0'} votes
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
