import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import VotingSystemABI from './abi/VotingSystem.json';

const VotingSystem = () => {
  const [provider, setProvider] = useState(null);
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState('');
  const [polls, setPolls] = useState([]);
  const [newPoll, setNewPoll] = useState({ title: '', options: [], duration: 60 });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const contractAddress = '0x002A13DF3bB8Ce7D7143bE17223F8893e2152c29';

  useEffect(() => {
    if (window.ethereum) {
      const ethereumProvider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(ethereumProvider);
    }
  }, []);

  const connectWallet = async () => {
    try {
      const accounts = await window.ethereum.request({
        method: 'eth_requestAccounts',
      });
      setAccount(accounts[0]);

      const signer = provider.getSigner();
      const votingContract = new ethers.Contract(
        contractAddress,
        VotingSystemABI.abi,
        signer
      );
      setContract(votingContract);
    } catch (err) {
      setError(err.message);
    }
  };

  const createPoll = async () => {
    try {
      setLoading(true);
      const tx = await contract.createPoll(
        newPoll.title,
        newPoll.options,
        newPoll.duration
      );
      await tx.wait();
      setNewPoll({ title: '', options: [], duration: 60 });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const vote = async (pollId, optionIndex) => {
    try {
      setLoading(true);
      const tx = await contract.vote(pollId, optionIndex);
      await tx.wait();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const getPollDetails = async (pollId) => {
    try {
      const details = await contract.getPollDetails(pollId);
      return {
        title: details[0],
        options: details[1],
        endTime: details[2].toNumber(),
        creator: details[3]
      };
    } catch (err) {
      setError(err.message);
      return null;
    }
  };

  const getPollResults = async (pollId) => {
    try {
      const results = await contract.getPollResults(pollId);
      return results.map(r => r.toNumber());
    } catch (err) {
      setError(err.message);
      return [];
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Blockchain Voting System</h1>

      {!account ? (
        <button
          onClick={connectWallet}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Connect Wallet
        </button>
      ) : (
        <div className="space-y-6">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">Create New Poll</h2>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Poll Title"
                value={newPoll.title}
                onChange={(e) => setNewPoll({...newPoll, title: e.target.value})}
                className="w-full p-2 border rounded"
              />
              <textarea
                placeholder="Poll Options (one per line)"
                value={newPoll.options.join('\n')}
                onChange={(e) => setNewPoll({...newPoll, options: e.target.value.split('\n')})}
                className="w-full p-2 border rounded h-32"
              />
              <input
                type="number"
                placeholder="Duration (minutes)"
                value={newPoll.duration}
                onChange={(e) => setNewPoll({...newPoll, duration: parseInt(e.target.value)})}
                className="w-full p-2 border rounded"
              />
              <button
                onClick={createPoll}
                disabled={loading}
                className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded disabled:opacity-50"
              >
                {loading ? 'Creating...' : 'Create Poll'}
              </button>
            </div>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
              {error}
            </div>
          )}

          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">Active Polls</h2>
            {polls.map((poll) => (
              <div key={poll.id} className="border-b py-4">
                <h3 className="text-lg font-medium">{poll.title}</h3>
                <div className="mt-2 space-y-2">
                  {poll.options.map((option, index) => (
                    <button
                      key={index}
                      onClick={() => vote(poll.id, index)}
                      disabled={loading}
                      className="bg-blue-100 hover:bg-blue-200 text-blue-800 py-1 px-3 rounded mr-2"
                    >
                      {option}
                    </button>
                  ))}
                </div>
                <p className="text-sm text-gray-500 mt-2">
                  Ends: {new Date(poll.endTime * 1000).toLocaleString()}
                </p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default VotingSystem;
