import React, { useState } from 'react';
import ConnectWallet from './components/ConnectWallet';
import WalletDetails from './components/WalletDetails';
import { ethers } from 'ethers';

const App = () => {
  const [provider, setProvider] = useState(null);
  const [account, setAccount] = useState('');
  const [balance, setBalance] = useState('');

  const handleWalletConnected = async (provider, account) => {
    setProvider(provider);
    setAccount(account);

    // Fetch and format balance
    const balance = await provider.getBalance(account);
    setBalance(ethers.utils.formatEther(balance));
  };

  const disconnectWallet = () => {
    setProvider(null);
    setAccount('');
    setBalance('');
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Ethereum Wallet</h1>
      {!account ? (
        <ConnectWallet onWalletConnected={handleWalletConnected} />
      ) : (
        <WalletDetails account={account} balance={balance} disconnectWallet={disconnectWallet} />
      )}
    </div>
  );
};

export default App;
