import React, { useState, useEffect } from "react";
import { getContract } from "./blockchain";
import { providers, utils } from 'ethers';
import { injected } from './wallet';

const App = () => {
    const [walletAddress, setWalletAddress] = useState("");
    const [contract, setContract] = useState(null);
    const [polls, setPolls] = useState([]);
    const [newPoll, setNewPoll] = useState({ title: "", options: [], duration: 0 });
    const [pollResults, setPollResults] = useState({});

    return (
        <div>
            <h1>Blockchain Voting System</h1>
            <div>
                <h1>Ethereum Sepolia Connection</h1>
                {!active ? (
                    <button onClick={connectWallet}>Connect Wallet</button>
                ) : (
                    <button onClick={disconnectWallet}>Disconnect Wallet</button>
                )}
                {active && (
                    <div>
                        <p>Connected Account: {account}</p>
                        <button onClick={getAccountBalance}>Get Balance</button>
                    </div>
                )}
            </div>

            <div>
                <h2>Available Polls</h2>
                {polls.map((poll) => (
                    <div key={poll.id}>
                        <h3>{poll.title}</h3>
                        <p>Creator: {poll.creator}</p>
                        <p>Ends: {new Date(poll.endTime * 1000).toLocaleString()}</p>
                        <div>
                            {(poll.options && Array.isArray(poll.options)) ? (
                                poll.options.map((option, index) => (
                                    <button
                                        key={index}
                                        onClick={() => voteForOption(poll.id, index)}
                                    >
                                        {option}
                                    </button>
                                ))
                            ) : (
                                <p>No options available</p>
                            )}
                        </div>
                        <button onClick={() => fetchPollResults(poll.id)}>View Results</button>
                        {pollResults[poll.id] && (
                            <div>
                                <h4>Results</h4>
                                {pollResults[poll.id].map((votes, index) => (
                                    <p key={index}>
                                        {poll.options[index]}: {votes} votes
                                    </p>
                                ))}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default App;
