import React, { useState, useEffect } from "react";
import { getContract } from "./blockchain";
import { client } from "./client";
import { providers, utils } from 'ethers';
import { injected } from './wallet';

const App = () => {
    const [walletAddress, setWalletAddress] = useState("");
    const [contract, setContract] = useState(null);
    const [polls, setPolls] = useState([]);
    const [newPoll, setNewPoll] = useState({ title: "", options: [], duration: 0 });
    const [pollResults, setPollResults] = useState({});

    const connectWallet = async () => {
        try {
            await activate(injected);
        } catch (err) {
            console.error("Error connecting wallet:", err);
        }
    };

    const disconnectWallet = () => {
        try {
            deactivate();
        } catch (err) {
            console.error("Error disconnecting wallet:", err);
        }
    };

    const getAccountBalance = async () => {
        if (library) {
            const provider = new providers.Web3Provider(library.provider);
            const balance = await provider.getBalance(account);
            console.log("Balance:", utils.formatEther(balance));
        }
    };

    useEffect(() => {
        if (active) {
            console.log(`Connected to account: ${account}`);
        }
    }, [active, account]);

    const fetchPolls = async () => {
        if (!contract) return;
        try {
            const pollList = [];
            for (let i = 0; i < 10; i++) {
                try {
                    const [title, options, endTime, creator] = await contract.getPollDetails(i);
                    pollList.push({
                        id: i,
                        title: title || "No Title",
                        options: Array.isArray(options) ? options : [],
                        endTime,
                        creator: creator || "Unknown Creator",
                    });
                } catch (error) {
                    console.error("Error fetching poll details for ID:", i, error);
                    break;
                }
            }
            setPolls(pollList);
        } catch (error) {
            console.error("Error fetching polls:", error);
        }
    };

    const createPoll = async () => {
        if (!contract) return;
        try {
            const tx = await contract.createPoll(
                newPoll.title,
                newPoll.options,
                newPoll.duration
            );
            await tx.wait();
            alert("Poll created successfully!");
            fetchPolls();
        } catch (error) {
            console.error("Error creating poll:", error);
        }
    };

    const voteForOption = async (pollId, optionIndex) => {
        if (!contract) return;
        try {
            const tx = await contract.vote(pollId, optionIndex);
            await tx.wait();
            alert("Vote cast successfully!");
            fetchPolls();
        } catch (error) {
            console.error("Error voting:", error);
        }
    };

    const fetchPollResults = async (pollId) => {
        if (!contract) return;
        try {
            const results = await contract.getPollResults(pollId);
            setPollResults((prevResults) => ({
                ...prevResults,
                [pollId]: results,
            }));
        } catch (error) {
            console.error("Error fetching results:", error);
        }
    };

    useEffect(() => {
        if (contract) fetchPolls();
    }, [contract]);

    return (
        <div>
            <h1>Blockchain Voting System</h1>
            <div>
                <h1>Ethereum Sepolia Connection</h1>
                {!active ? (
                    <button onClick={connectWallet}>Connect Wallet</button>
                ) : (
                    <button onClick={disconnectWallet}>Disconnect Wallet</button>
                )}
                {active && (
                    <div>
                        <p>Connected Account: {account}</p>
                        <button onClick={getAccountBalance}>Get Balance</button>
                    </div>
                )}
            </div>
            <div>
                <h2>Create a Poll</h2>
                <input
                    type="text"
                    placeholder="Poll Title"
                    value={newPoll.title}
                    onChange={(e) => setNewPoll({ ...newPoll, title: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Options (comma-separated)"
                    value={newPoll.options.join(", ")}
                    onChange={(e) =>
                        setNewPoll({
                            ...newPoll,
                            options: e.target.value.split(",").map((opt) => opt.trim()),
                        })
                    }
                />
                <input
                    type="number"
                    placeholder="Duration (minutes)"
                    value={newPoll.duration}
                    onChange={(e) =>
                        setNewPoll({ ...newPoll, duration: parseInt(e.target.value) })
                    }
                />
                <button onClick={createPoll}>Create Poll</button>
            </div>
            <div>
                <h2>Available Polls</h2>
                {polls.map((poll) => (
                    <div key={poll.id}>
                        <h3>{poll.title}</h3>
                        <p>Creator: {poll.creator}</p>
                        <p>Ends: {new Date(poll.endTime * 1000).toLocaleString()}</p>
                        <div>
                            {(poll.options && Array.isArray(poll.options)) ? (
                                poll.options.map((option, index) => (
                                    <button
                                        key={index}
                                        onClick={() => voteForOption(poll.id, index)}
                                    >
                                        {option}
                                    </button>
                                ))
                            ) : (
                                <p>No options available</p>
                            )}
                        </div>
                        <button onClick={() => fetchPollResults(poll.id)}>View Results</button>
                        {pollResults[poll.id] && (
                            <div>
                                <h4>Results</h4>
                                {pollResults[poll.id].map((votes, index) => (
                                    <p key={index}>
                                        {poll.options[index]}: {votes} votes
                                    </p>
                                ))}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default App;
