import React, { useState, useEffect } from "react";
import { ethers } from "ethers";

const App = () => {
  const [walletAddress, setWalletAddress] = useState("");
  const [provider, setProvider] = useState(null);
  const [active, setActive] = useState(false);
  const [balance, setBalance] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    if (window.ethereum) {
      // Listen for account or network change
      window.ethereum.on("accountsChanged", (accounts) => {
        setWalletAddress(accounts[0]);
      });

      window.ethereum.on("chainChanged", (chainId) => {
        // Ensure the user is connected to the Sepolia testnet
        if (chainId === "0x111") { // Sepolia Testnet Chain ID
          setActive(true);
          setError(""); // Clear any errors
        } else {
          setActive(false);
          setError("Please switch to Sepolia Testnet in MetaMask.");
        }
      });
    } else {
      setError("MetaMask is not installed. Please install MetaMask.");
    }
  }, []);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        setWalletAddress(accounts[0]);
        setActive(true);

        // Create a Web3 provider using MetaMask's injected provider
        const _provider = new ethers.BrowserProvider(window.ethereum);
        setProvider(_provider);
      } catch (error) {
        setError("Failed to connect wallet. Please try again.");
        console.error("Failed to connect wallet:", error);
      }
    } else {
      setError("MetaMask is not installed. Please install MetaMask.");
    }
  };

  const disconnectWallet = () => {
    setWalletAddress("");
    setActive(false);
    setBalance(null);
  };

  const getAccountBalance = async () => {
    if (provider && walletAddress) {
      try {
        const balanceBigInt = await provider.getBalance(walletAddress);
        const formattedBalance = ethers.formatEther(balanceBigInt);
        setBalance(formattedBalance);
      } catch (error) {
        setError("Failed to fetch balance.");
        console.error("Failed to fetch balance:", error);
      }
    }
  };

  return (
    <div>
      <h1>Blockchain Voting System</h1>
      <div>
        <h2>Ethereum Sepolia Testnet Connection</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}
        {!active ? (
          <button onClick={connectWallet}>Connect Wallet</button>
        ) : (
          <button onClick={disconnectWallet}>Disconnect Wallet</button>
        )}
        {active && (
          <div>
            <p>Connected Account: {walletAddress}</p>
            <button onClick={getAccountBalance}>Get Balance</button>
            {balance !== null && <p>Balance: {balance} ETH</p>}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
