import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import abi from '../abi/VotingSystem.json';

const VotingSystem = ({ provider, account }) => {
  const [contract, setContract] = useState(null);
  const [polls, setPolls] = useState([]);
  const [newPoll, setNewPoll] = useState({
    title: '',
    options: ['', ''],
    duration: 60
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (provider) {
      const contract = new ethers.Contract(
        "0x002A13DF3bB8Ce7D7143bE17223F8893e2152c29",
        abi,
        provider
      );
      setContract(contract);

      // Listen for new polls
      contract.on("PollCreated", (pollId, title, endTime, creator) => {
        loadPollDetails(pollId.toString());
      });

      // Load existing polls
      loadExistingPolls();

      return () => {
        contract.removeAllListeners();
      };
    }
  }, [provider]);

  const loadExistingPolls = async () => {
    try {
      const events = await contract.queryFilter("PollCreated");
      const pollPromises = events.map(event => loadPollDetails(event.args.pollId.toString()));
      await Promise.all(pollPromises);
    } catch (error) {
      console.error('Error loading existing polls:', error);
    }
  };

  const loadPollDetails = async (pollId) => {
    try {
      const details = await contract.getPollDetails(pollId);
      const results = await contract.getPollResults(pollId);

      setPolls(prevPolls => {
        const pollExists = prevPolls.some(p => p.pollId === pollId);
        if (!pollExists) {
          return [...prevPolls, {
            pollId,
            title: details.title,
            options: details.options,
            endTime: details.endTime,
            creator: details.creator,
            results
          }];
        }
        return prevPolls;
      });
    } catch (error) {
      console.error(`Error loading poll ${pollId}:`, error);
    }
  };

  const createPoll = async () => {
    try {
      setLoading(true);
      const signer = await provider.getSigner();
      const connectedContract = contract.connect(signer);

      const tx = await connectedContract.createPoll(
        newPoll.title,
        newPoll.options.filter(opt => opt !== ''),
        newPoll.duration
      );
      await tx.wait();

      setNewPoll({ title: '', options: ['', ''], duration: 60 });
    } catch (error) {
      console.error('Error creating poll:', error);
    } finally {
      setLoading(false);
    }
  };

  const castVote = async (pollId, optionIndex) => {
    try {
      setLoading(true);
      const signer = await provider.getSigner();
      const connectedContract = contract.connect(signer);

      const tx = await connectedContract.vote(pollId, optionIndex);
      await tx.wait();

      // Refresh poll results after voting
      await loadPollDetails(pollId);
    } catch (error) {
      console.error('Error voting:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="mb-8 bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Create New Poll</h2>
        <input
          type="text"
          value={newPoll.title}
          onChange={(e) => setNewPoll({...newPoll, title: e.target.value})}
          placeholder="Poll Title"
          className="w-full p-2 mb-4 border rounded"
        />
        {newPoll.options.map((option, index) => (
          <input
            key={index}
            type="text"
            value={option}
            onChange={(e) => {
              const newOptions = [...newPoll.options];
              newOptions[index] = e.target.value;
              setNewPoll({...newPoll, options: newOptions});
            }}
            placeholder={`Option ${index + 1}`}
            className="w-full p-2 mb-2 border rounded"
          />
        ))}
        <div className="flex items-center gap-4 mb-4">
          <input
            type="number"
            value={newPoll.duration}
            onChange={(e) => setNewPoll({...newPoll, duration: parseInt(e.target.value)})}
            placeholder="Duration (minutes)"
            className="p-2 border rounded"
          />
          <button
            onClick={() => setNewPoll({...newPoll, options: [...newPoll.options, '']})}
            className="bg-gray-500 text-white px-4 py-2 rounded"
          >
            Add Option
          </button>
        </div>
        <button
          onClick={createPoll}
          disabled={loading}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          {loading ? 'Creating...' : 'Create Poll'}
        </button>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h2 className="text-xl font-bold mb-4">Active Polls</h2>
        {polls.map((poll) => (
          <div key={poll.pollId} className="border-b py-4 last:border-b-0">
            <h3 className="font-bold">{poll.title}</h3>
            <p className="text-sm text-gray-600 mb-2">
              Created by: {poll.creator.slice(0, 6)}...{poll.creator.slice(-4)}
            </p>
            <div className="mt-2">
              {poll.options.map((option, optIndex) => (
                <button
                  key={optIndex}
                  onClick={() => castVote(poll.pollId, optIndex)}
                  className="bg-blue-100 hover:bg-blue-200 px-4 py-2 rounded mr-2 mb-2"
                >
                  {option} ({poll.results[optIndex].toString()} votes)
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default VotingSystem;
